mapper.select:
SELECT A.log_id as logId , A.device_id as deviceId , A.device_stat as deviceStat , A.log_datetime as logDatetime , C.area_name as areaName , B.device_name as deviceName , B.device_code as deviceCode , A.description as description FROM isaver.device_status_history A INNER JOIN isaver.device b ON a.device_id = b.device_id INNER JOIN isaver.area c ON b.area_id = c.area_id
AND a.device_id = #{deviceId}
AND c.area_id = #{areaId}
AND B.device_code = #{deviceCode}
AND a.device_stat = #{deviceStat}
AND A.log_datetime >= (#{startDatetimeStr} || ' ' || #{startDatetimeHour} ||':00:00')::timestamp
AND A.log_datetime <= (#{endDatetimeStr} || ' ' || #{endDatetimeHour} ||':59:59')::timestamp
ORDER BY A.log_datetime DESC
LIMIT ${pageRowNumber} OFFSET ${pageIndex} ;

SELECT  count(A.log_id)
FROM  isaver.device_status_history A
INNER JOIN isaver.device b
ON      a.device_id = b.device_id
INNER JOIN isaver.area c
ON      b.area_id = c.area_id


        AND     a.device_id = #{deviceId}


        AND     c.area_id = #{areaId}


        AND     B.device_code = #{deviceCode}


        AND     a.device_stat = #{deviceStat}


        AND     A.log_datetime  >=  (#{startDatetimeStr} || ' ' || #{startDatetimeHour} ||':00:00')::timestamp


        AND     A.log_datetime  <=  (#{endDatetimeStr} || ' ' || #{endDatetimeHour} ||':59:59')::timestamp
