mapper.select:
SELECT A.uuid as uuid , A.fence_id as fenceId , A.fence_type as fenceType , A.fence_sub_type as fenceSubType , A.device_id as deviceId , C.area_id as areaId , A.fence_name as fenceName , A.z_min as zMin , A.config as config , A.custom_json as customJson , b.lat as lat , b.lng as lng FROM isaver.fence A INNER JOIN isaver.fence_location b ON a.uuid = b.uuid INNER JOIN isaver.device c ON a.device_id = c.device_id
AND a.device_id = #{deviceId}
AND c.area_id = #{areaId}
AND a.fence_type = #{fenceType}

mapper.insert:
INSERT INTO isaver.fence (
    uuid
    , fence_id
    , fence_type
    , fence_sub_type
    , device_id
    , fence_name
    , z_min
    , config
    , custom_json
) VALUES

    (
        #{item.uuid}
        ,#{item.fenceId}
        ,#{item.fenceType}
        ,#{item.fenceSubType}
        ,#{item.deviceId}
        ,#{item.fenceName}

            ,${item.zMin}


            ,0


            ,#{item.config}


            ,null


            ,#{item.customJson}


            ,null

    )

mapper.update:
UPDATE  isaver.fence
SET     device_id = #{deviceId}
WHERE   uuid in (
    SELECT  uuid
    FROM    isaver.fence
    WHERE   device_id in (
        SELECT  device_id
        FROM    isaver.device
        WHERE   parent_device_id = #{parentDeviceId}
    )
);

mapper.delete:
DELETE FROM isaver.fence
WHERE  device_id in (
    SELECT  device_id
    FROM    isaver.device
    WHERE   area_id = #{areaId}
);
