mapper.select:
SELECT A.notification_id as notificationId , A.event_log_id as eventLogId , A.object_id as objectId , A.fence_id as fenceId , (SELECT fence_name FROM isaver.fence f WHERE A.fence_id = f.fence_id AND A.device_id = f.device_id) as fenceName , A.status as status , A.area_id as areaId , AREA.area_name as areaName , A.device_id as deviceId , DEVICE.device_name as deviceName , A.event_id as eventId , EVENT.event_name as eventName , A.event_datetime as eventDatetime , to_char(A.event_datetime,'yyyy-MM-dd HH24:MI:ss') as eventDatetimeStr , A.critical_level as criticalLevel , COD.code_name as criticalLevelName , A.confirm_user_id as confirmUserId , (select user_name from isaver.users where user_id=A.confirm_user_id) as confirmUserName , A.confirm_datetime as confirmDatetime , to_char(A.confirm_datetime,'yyyy-MM-dd HH24:MI:ss') as confirmDatetimeStr , A.cancel_user_id as cancelUserId , (select user_name from isaver.users where user_id=A.cancel_user_id) as cancelUserName , A.cancel_datetime as cancelDatetime , to_char(A.cancel_datetime,'yyyy-MM-dd HH24:MI:ss') as cancelDatetimeStr , A.cancel_desc as cancelDesc , A.insert_user_id as insertUserId , (select user_name from isaver.users where user_id=A.insert_user_id) as insertUserName , A.insert_datetime as insertDatetime , A.update_user_id as updateUserId , (select user_name from isaver.users where user_id=A.update_user_id) as updateUserName , A.update_datetime as updateDatetime , A.value as value , A.tracking_json as trackingJson , (select count(*) from external.noti_send_log sendLog where A.notification_id = sendLog.notification_id) as sendCnt FROM isaver.notification as A INNER JOIN isaver.area AREA ON A.area_id = AREA.area_id INNER JOIN isaver.code COD ON A.critical_level = COD.code_id AND COD.group_code_id = 'LEV' INNER JOIN isaver.device AS DEVICE ON A.device_id = DEVICE.device_id INNER JOIN isaver.event EVENT ON A.event_id = EVENT.event_id
AND AREA.area_id = #{areaId}
AND DEVICE.device_code = #{deviceCode}
AND A.event_id = #{eventId}
AND A.fence_id = #{fenceId}
AND A.critical_level = #{criticalLevel}
AND A.event_datetime >= (#{startDatetimeStr} || ' ' || #{startDatetimeHour} ||':00:00')::timestamp
AND A.event_datetime <= (#{endDatetimeStr} || ' ' || #{endDatetimeHour} ||':59:59')::timestamp
order by A.event_datetime DESC
LIMIT ${pageRowNumber} OFFSET ${pageIndex} ;

SELECT count(*) FROM isaver.notification as A INNER JOIN isaver.area AREA ON A.area_id = AREA.area_id INNER JOIN isaver.code COD ON A.critical_level = COD.code_id AND COD.group_code_id = 'LEV' INNER JOIN ( SELECT DEV.device_id, DEV.device_code, CODE.code_name FROM isaver.device DEV INNER JOIN isaver.code CODE ON DEV.device_code = CODE.code_id AND CODE.group_code_id = 'DEV' ) AS DEVICE ON A.device_id = DEVICE.device_id INNER JOIN isaver.event EVENT ON A.event_id = EVENT.event_id
AND AREA.area_id = #{areaId}
AND DEVICE.device_code = #{deviceCode}
AND A.event_id = #{eventId}
AND A.fence_id = #{fenceId}
AND A.critical_level = #{criticalLevel}
AND A.event_datetime >= (#{startDatetimeStr} || ' ' || #{startDatetimeHour} ||':00:00')::timestamp
AND A.event_datetime <= (#{endDatetimeStr} || ' ' || #{endDatetimeHour} ||':59:59')::timestamp

update.foreach:
update  isaver.notification
set     
            confirm_user_id = #{item.updateUserId}
            , confirm_datetime = #{item.updateDatetime}::timestamp


            cancel_user_id = #{item.updateUserId}
            , cancel_datetime = #{item.updateDatetime}::timestamp

                , cancel_desc = #{item.cancelDesc}


where   notification_id = #{item.notificationId}

mapper.update:
update  isaver.notification
set     cancel_user_id = #{updateUserId}
        , cancel_datetime = now()
        , cancel_desc = #{cancelDesc}
where   cancel_user_id is null

    AND area_id in

        #{item}



    AND critical_level = #{criticalLevel}
