update.foreach:
WITH upsert as (
    UPDATE  isaver.template_setting
    SET

        value = #{item.value}


        value = null

    WHERE   setting_id = #{item.settingId}
    returning *
)
INSERT INTO isaver.template_setting (setting_id, value)
SELECT      #{item.settingId}

                ,#{item.value}


                ,null

WHERE NOT EXISTS (
    SELECT  setting_id, value
    FROM    isaver.template_setting
    WHERE   setting_id = #{item.settingId}
);
