mapper.select:
SELECT a.video_history_id as videoHistoryId ,a.notification_id as notificationId ,a.device_id as deviceId ,area.area_name as areaName ,b.event_name as eventName ,b.fence_name as fenceName ,c.device_name as deviceName ,a.video_file_name as videoFileName ,a.thumbnail_file_name as thumbnailFileName ,a.video_type as videoType ,a.video_datetime as videoDatetime ,a.video_size as videoSize FROM isaver.video_history a LEFT OUTER JOIN ( SELECT notification_id ,event_name ,fence_name FROM isaver.notification noti INNER JOIN isaver.event evt ON noti.event_id = evt.event_id LEFT OUTER JOIN isaver.fence fnc ON noti.fence_id = fnc.fence_id ) b ON a.notification_id = b.notification_id INNER JOIN isaver.device c ON a.device_id = c.device_id INNER JOIN isaver.area area ON c.area_id = area.area_id
AND area.area_id = #{areaId}
AND c.device_id = #{deviceId}
AND a.video_type = #{videoType}
AND a.video_datetime >= (#{startDatetimeStr} || ' ' || #{startDatetimeHour} ||':00:00')::timestamp
AND a.video_datetime <= (#{endDatetimeStr} || ' ' || #{endDatetimeHour} ||':59:59')::timestamp
AND a.notification_id = #{notificationId}
ORDER BY video_datetime DESC

SELECT count(a.video_history_id) FROM isaver.video_history a LEFT OUTER JOIN ( SELECT notification_id ,event_name FROM isaver.notification noti INNER JOIN isaver.event evt ON noti.event_id = evt.event_id ) b ON a.notification_id = b.notification_id INNER JOIN isaver.device c ON a.device_id = c.device_id INNER JOIN isaver.area area ON c.area_id = area.area_id
AND area.area_id = #{areaId}
AND c.device_id = #{deviceId}
AND a.video_type = #{videoType}
AND a.video_datetime >= (#{startDatetimeStr} || ' ' || #{startDatetimeHour} ||':00:00')::timestamp
AND a.video_datetime <= (#{endDatetimeStr} || ' ' || #{endDatetimeHour} ||':59:59')::timestamp
